import React, {useState, useEffect} from "react";

function Seconds (){
    const [counter, setCounter] = useState(0);
    useEffect(()=>{
        setInterval(()=>{
            setCounter(prevValue=>{
               return prevValue + 1;
            })
        }, 1000);
    })
    return(<span>This is a counter {counter}</span>)
}
export default Seconds;
